import React, { useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const AchievementsPage = styled.div`
  min-height: 100vh;
  background-color: #0a0a0a;
  color: #fff;
  padding: 2rem 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const Header = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 3rem;
`;

const Logo = styled(Link)`
  font-family: 'Orbitron', sans-serif;
  font-size: 1.8rem;
  font-weight: 700;
  color: #ff4500;
  display: flex;
  align-items: center;
  
  &:hover {
    text-shadow: 0 0 10px #ff4500;
  }
`;

const Navigation = styled.nav`
  display: flex;
  gap: 1.5rem;
`;

const NavLink = styled(Link)<{ $active?: boolean }>`
  font-family: 'Orbitron', sans-serif;
  font-size: 1.1rem;
  color: ${props => props.$active ? '#ff4500' : '#fff'};
  position: relative;
  padding: 0.5rem 0;
  
  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: ${props => props.$active ? '100%' : '0'};
    height: 2px;
    background-color: #ff4500;
    transition: width 0.3s ease;
  }
  
  &:hover:after {
    width: 100%;
  }
`;

const Title = styled(motion.h1)`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 2rem;
  text-transform: uppercase;
  background: linear-gradient(90deg, #ff4500, #ff8700);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  
  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const AchievementsList = styled(motion.div)`
  margin-top: 3rem;
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
`;

const AchievementCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 15px;
  padding: 2rem;
  position: relative;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  
  &:before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 5px;
    height: 100%;
    background: #ff4500;
  }
  
  &:hover {
    background: rgba(255, 255, 255, 0.08);
  }
`;

const AchievementHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
  
  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
  }
`;

const AchievementTitle = styled.h2`
  font-size: 1.8rem;
  color: #fff;
`;

const AchievementDate = styled.span`
  padding: 5px 15px;
  background: rgba(255, 69, 0, 0.2);
  color: #ff4500;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: 500;
`;

const AchievementDescription = styled.p`
  font-size: 1.1rem;
  line-height: 1.7;
  color: #cccccc;
  margin-bottom: 1.5rem;
`;

const AchievementTags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 1rem;
`;

const AchievementTag = styled.span`
  padding: 5px 12px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  font-size: 0.85rem;
  color: #aaaaaa;
`;

const CertificationsSection = styled(motion.section)`
  margin-top: 5rem;
`;

const CertificationsTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 2rem;
  color: #fff;
`;

const CertificationsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const CertificationCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(255, 69, 0, 0.2);
  }
`;

const CertificationImage = styled.div<{ $image: string }>`
  width: 100%;
  height: 200px;
  background-image: url(${props => props.$image});
  background-size: cover;
  background-position: center;
  border-bottom: 3px solid #ff4500;
`;

const CertificationContent = styled.div`
  padding: 1.5rem;
`;

const CertificationTitle = styled.h3`
  font-size: 1.3rem;
  margin-bottom: 10px;
  color: #fff;
`;

const CertificationIssuer = styled.p`
  font-size: 1rem;
  color: #aaaaaa;
  margin-bottom: 10px;
`;

const CertificationDate = styled.span`
  display: inline-block;
  font-size: 0.9rem;
  color: #ff4500;
`;

const AwardsList = styled(motion.div)`
  margin-top: 4rem;
`;

const AwardsTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 2rem;
  color: #fff;
`;

const AwardItem = styled(motion.div)`
  display: flex;
  margin-bottom: 2rem;
  padding: 1.5rem;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 15px;
  border-left: 5px solid #ff4500;
  transition: all 0.3s ease;
  
  &:hover {
    background: rgba(255, 255, 255, 0.08);
    transform: translateX(10px);
  }
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const AwardIcon = styled.div`
  font-size: 2.5rem;
  margin-right: 1.5rem;
  color: #ff4500;
  
  @media (max-width: 768px) {
    margin-bottom: 1rem;
  }
`;

const AwardContent = styled.div`
  flex: 1;
`;

const AwardName = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 10px;
  color: #fff;
`;

const AwardInfo = styled.p`
  font-size: 1rem;
  color: #cccccc;
`;

// –î–∞–Ω–Ω—ã–µ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π
const achievements = [
  {
    id: 1,
    title: "–ó–∞–ø—É—Å–∫ –≤—ã—Å–æ–∫–æ–Ω–∞–≥—Ä—É–∂–µ–Ω–Ω–æ–≥–æ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è",
    date: "2023",
    description: "–£—Å–ø–µ—à–Ω–æ —Å–ø—Ä–æ–µ–∫—Ç–∏—Ä–æ–≤–∞–ª –∏ —Ä–∞–∑—Ä–∞–±–æ—Ç–∞–ª –∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä—É –≤–µ–±-–ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è, —Å–ø–æ—Å–æ–±–Ω–æ–≥–æ –≤—ã–¥–µ—Ä–∂–∏–≤–∞—Ç—å –Ω–∞–≥—Ä—É–∑–∫—É –≤ 1 –º–∏–ª–ª–∏–æ–Ω –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –æ–¥–Ω–æ–≤—Ä–µ–º–µ–Ω–Ω–æ. –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–ª –∫–æ–¥ –∏ –∏–Ω—Ñ—Ä–∞—Å—Ç—Ä—É–∫—Ç—É—Ä—É –¥–ª—è –±—ã—Å—Ç—Ä–æ–π –º–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º–æ—Å—Ç–∏.",
    tags: ["–ê—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä–∞", "–û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è", "–ú–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–∏–µ", "Cloud"]
  },
  {
    id: 2,
    title: "–û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏",
    date: "2022",
    description: "–£–≤–µ–ª–∏—á–∏–ª —Å–∫–æ—Ä–æ—Å—Ç—å –∑–∞–≥—Ä—É–∑–∫–∏ –∫–ª—é—á–µ–≤—ã—Ö —Å—Ç—Ä–∞–Ω–∏—Ü –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è –Ω–∞ 70%, —á—Ç–æ –ø—Ä–∏–≤–µ–ª–æ –∫ —É–≤–µ–ª–∏—á–µ–Ω–∏—é –∫–æ–Ω–≤–µ—Ä—Å–∏–∏ –Ω–∞ 25%. –í–Ω–µ–¥—Ä–∏–ª –ø—Ä–æ–≥—Ä–µ—Å—Å–∏–≤–Ω—ã–µ –≤–µ–±-—Ç–µ—Ö–Ω–æ–ª–æ–≥–∏–∏ –∏ –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–ª FCP –∏ TTI.",
    tags: ["–ü—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å", "Web Vitals", "UX", "–ê–Ω–∞–ª–∏—Ç–∏–∫–∞"]
  },
  {
    id: 3,
    title: "–í–Ω–µ–¥—Ä–µ–Ω–∏–µ –º–∏–∫—Ä–æ—Å–µ—Ä–≤–∏—Å–Ω–æ–π –∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä—ã",
    date: "2021",
    description: "–†—É–∫–æ–≤–æ–¥–∏–ª –º–∏–≥—Ä–∞—Ü–∏–µ–π –º–æ–Ω–æ–ª–∏—Ç–Ω–æ–≥–æ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è –Ω–∞ –º–∏–∫—Ä–æ—Å–µ—Ä–≤–∏—Å–Ω—É—é –∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä—É, —á—Ç–æ –ø–æ–∑–≤–æ–ª–∏–ª–æ —É—Å–∫–æ—Ä–∏—Ç—å –ø—Ä–æ—Ü–µ—Å—Å —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–∏ –≤ 3 —Ä–∞–∑–∞ –∏ –ø–æ–≤—ã—Å–∏—Ç—å –Ω–∞–¥–µ–∂–Ω–æ—Å—Ç—å —Å–∏—Å—Ç–µ–º—ã.",
    tags: ["–ú–∏–∫—Ä–æ—Å–µ—Ä–≤–∏—Å—ã", "DevOps", "Docker", "Kubernetes"]
  }
];

// –î–∞–Ω–Ω—ã–µ —Å–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç–æ–≤
const certifications = [
  {
    id: 1,
    title: "Advanced React & Redux",
    issuer: "Udemy",
    date: "2023",
    image: "https://placehold.co/600x400/111/333?text=React+Redux"
  },
  {
    id: 2,
    title: "AWS Certified Solutions Architect",
    issuer: "Amazon Web Services",
    date: "2022",
    image: "https://placehold.co/600x400/111/333?text=AWS+Certificate"
  },
  {
    id: 3,
    title: "Professional Scrum Master",
    issuer: "Scrum.org",
    date: "2021",
    image: "https://placehold.co/600x400/111/333?text=Scrum+Master"
  }
];

// –î–∞–Ω–Ω—ã–µ –Ω–∞–≥—Ä–∞–¥
const awards = [
  {
    id: 1,
    name: "–õ—É—á—à–∏–π —Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫ –≥–æ–¥–∞",
    info: "–ü—Ä–∏–∑–Ω–∞–Ω –ª—É—á—à–∏–º —Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫–æ–º –≥–æ–¥–∞ –Ω–∞ –µ–∂–µ–≥–æ–¥–Ω–æ–π –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–æ–π –∫–æ–Ω—Ñ–µ—Ä–µ–Ω—Ü–∏–∏ –∑–∞ –≤—ã–¥–∞—é—â–∏–π—Å—è –≤–∫–ª–∞–¥ –≤ —Ä–∞–∑–≤–∏—Ç–∏–µ –ø—Ä–æ–¥—É–∫—Ç–∞.",
    icon: "üèÜ"
  },
  {
    id: 2,
    name: "–•–∞–∫–∞—Ç–æ–Ω –ü–æ–±–µ–¥–∏—Ç–µ–ª—å",
    info: "–ü–µ—Ä–≤–æ–µ –º–µ—Å—Ç–æ –Ω–∞ –º–µ–∂–¥—É–Ω–∞—Ä–æ–¥–Ω–æ–º —Ö–∞–∫–∞—Ç–æ–Ω–µ –ø–æ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ –∏–Ω–Ω–æ–≤–∞—Ü–∏–æ–Ω–Ω—ã—Ö —Ä–µ—à–µ–Ω–∏–π –≤ —Å—Ñ–µ—Ä–µ —Ñ–∏–Ω—Ç–µ—Ö.",
    icon: "ü•á"
  },
  {
    id: 3,
    name: "–ò–Ω–Ω–æ–≤–∞—Ç–æ—Ä –≥–æ–¥–∞",
    info: "–ù–∞–≥—Ä–∞–¥–∞ –∑–∞ –≤–Ω–µ–¥—Ä–µ–Ω–∏–µ –Ω–æ–≤–∞—Ç–æ—Ä—Å–∫–æ–≥–æ –ø–æ–¥—Ö–æ–¥–∞ –∫ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ, –∑–Ω–∞—á–∏—Ç–µ–ª—å–Ω–æ –ø–æ–≤—ã—Å–∏–≤—à–µ–≥–æ —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –∫–æ–º–∞–Ω–¥—ã.",
    icon: "üí°"
  }
];

const Achievements: React.FC = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
  };

  return (
    <AchievementsPage>
      <Container>
        <Header>
          <Logo to="/">Doom Tech</Logo>
          <Navigation>
            <NavLink to="/">–ì–ª–∞–≤–Ω–∞—è</NavLink>
            <NavLink to="/about">–û–±–æ –º–Ω–µ</NavLink>
            <NavLink to="/experience">–û–ø—ã—Ç</NavLink>
            <NavLink to="/portfolio">–ü–æ—Ä—Ç—Ñ–æ–ª–∏–æ</NavLink>
            <NavLink to="/achievements" $active={true}>–î–æ—Å—Ç–∏–∂–µ–Ω–∏—è</NavLink>
          </Navigation>
        </Header>
        
        <Title
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          –î–æ—Å—Ç–∏–∂–µ–Ω–∏—è
        </Title>
        
        <AchievementsList
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          {achievements.map((achievement, index) => (
            <AchievementCard
              key={achievement.id}
              variants={itemVariants}
              whileHover={{ x: 10 }}
              transition={{ duration: 0.3 }}
            >
              <AchievementHeader>
                <AchievementTitle>{achievement.title}</AchievementTitle>
                <AchievementDate>{achievement.date}</AchievementDate>
              </AchievementHeader>
              <AchievementDescription>{achievement.description}</AchievementDescription>
              <AchievementTags>
                {achievement.tags.map((tag, i) => (
                  <AchievementTag key={i}>{tag}</AchievementTag>
                ))}
              </AchievementTags>
            </AchievementCard>
          ))}
        </AchievementsList>
        
        <CertificationsSection
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          <CertificationsTitle>–°–µ—Ä—Ç–∏—Ñ–∏–∫–∞—Ç—ã</CertificationsTitle>
          <CertificationsGrid>
            {certifications.map((cert, index) => (
              <CertificationCard
                key={cert.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ 
                  opacity: 1, 
                  y: 0,
                  transition: { 
                    duration: 0.5,
                    delay: 0.4 + (index * 0.1)
                  }
                }}
              >
                <CertificationImage $image={cert.image} />
                <CertificationContent>
                  <CertificationTitle>{cert.title}</CertificationTitle>
                  <CertificationIssuer>{cert.issuer}</CertificationIssuer>
                  <CertificationDate>{cert.date}</CertificationDate>
                </CertificationContent>
              </CertificationCard>
            ))}
          </CertificationsGrid>
        </CertificationsSection>
        
        <AwardsList
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.6 }}
        >
          <AwardsTitle>–ù–∞–≥—Ä–∞–¥—ã</AwardsTitle>
          
          {awards.map((award, index) => (
            <AwardItem
              key={award.id}
              initial={{ opacity: 0, x: -30 }}
              animate={{ 
                opacity: 1, 
                x: 0,
                transition: { 
                  duration: 0.5,
                  delay: 0.7 + (index * 0.1)
                }
              }}
            >
              <AwardIcon>{award.icon}</AwardIcon>
              <AwardContent>
                <AwardName>{award.name}</AwardName>
                <AwardInfo>{award.info}</AwardInfo>
              </AwardContent>
            </AwardItem>
          ))}
        </AwardsList>
      </Container>
    </AchievementsPage>
  );
};

export default Achievements;
